import React, { useState, useEffect } from 'react'
import './Profile.css'

const Profile = () => {
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        fetchCurrentUser();
        // fetch('http://localhost:4000/currentuser')
        // .then((response) => response.json())
        // .then((data) => {
        //     setCurrentUser(data);
        // })
        // .catch((error) => {
        //     console.error('Error fetching data:', error);
        // });
    }, []);

    const fetchCurrentUser = async () => {
        try {
            const token = localStorage.getItem('auth-token');

            if (token) {
                const response = await fetch('http://localhost:4000/currentuser', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                const responseData = await response.json();

                console.log("User details fetched: ")
                console.log(responseData.user);

                if (responseData.success) {
                    setCurrentUser(responseData.user);
                } else {
                    console.error('Error fetching current user:', responseData.errors);
                }
            }
        } catch (error) {
            console.error('Error fetching current user:', error);
        }
    };

    return (
        <div className='profile'>
            <div className="profile-container">
                <h1>My Profile</h1>
                <div className="profile-details">
                    <input type="text" name='username' placeholder='Enter your name' value={currentUser?.username || ""} />
                    <input type="email" name='email' placeholder='Email' value={currentUser?.email || ""} />
                    <input type="text" name='address' placeholder='Address' value={currentUser?.address || ""} />
                    <input type="tel" name='contact' placeholder='Phone No.' value={currentUser?.contact || ""} />
                </div>
                <button>Save</button>
            </div>
        </div>
    );
};

export default Profile;
