// CheckoutForm.js
import React, { useState } from 'react';

const CheckoutForm = ({ onCheckout, onCancel }) => {
  const [address, setAddress] = useState('');

  const handleAddressChange = (e) => {
    setAddress(e.target.value);
  };

  const handleSubmit = () => {
    // Assuming a function `onCheckout` is passed as a prop to handle the checkout
    onCheckout(address);
  };

  const handleCancel = () => {
    // Assuming a function `onCancel` is passed as a prop to handle cancellation
    onCancel();
  };

  return (
    <div>
      <h2>Enter Your Address</h2>
      <input type="text" placeholder="Enter your address" value={address} onChange={handleAddressChange} />
      <button onClick={handleSubmit}>Submit</button>
      <button onClick={handleCancel}>Cancel</button>
    </div>
  );
};

export default CheckoutForm;
