import React, { useContext, useState } from 'react'
import './ProductDisplay.css'
import { ShopContext } from '../../Context/ShopContext'

const ProductDisplay = (props) => {
    const {product} = props;
    const {addToCart} = useContext(ShopContext);

    const relatedImages = product.relatedImages;

    const [selectedImage, setSelectedImage] = useState(product.relatedImages[0]);

    const handleImageClick = (image) => {
      setSelectedImage(image);
    };

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          {relatedImages.length > 0 && relatedImages.map((element, index) => (
            <img key={index} 
            src={element} 
            alt="" 
            onClick={() => handleImageClick(element)}
              className={element === selectedImage ? 'selected' : ''}
            />
          ))}
        </div>
        <div className="productdisplay-img">
            <img className='productdisplay-main-img' src={product.image} alt="" />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <div className="productdisplay-right-prices">
        <p>Price :</p>
            <div className="productdisplay-right-price-old">
                ${product.old_price}
            </div>
            <div className="productdisplay-right-price-new">
                ${product.new_price}
            </div>
        </div>
        <div className="productdisplay-right-description">
          <p>About product :</p>
          {product.description}
        </div>
        <button onClick={()=>{addToCart(product.id)}}>ADD TO CART</button>
      </div>
    </div>
  )
}

export default ProductDisplay
