import React, { useEffect, useState } from 'react'
import './Popular.css'
import Item from '../Items/Item'
import background_img from '../Assets/background_img.jpg'



const Popular = () => {

  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '10px',
    height: '90vh',
    marginTop: '30px',
    position: 'relative',
  };

  const backgroundOverlayStyle = {
    content: "",
    position: "absolute",
    width: "100%",
    height: "100%",
    backgroundImage: "url('../Assets/background_img.jpg')",
    backgroundSize: "contain",
    backgroundColor: "rgba(255, 255, 255, 0.6)", // Adjust the alpha value for reduced opacity
    zIndex: -1,
  };

  const [popularProducts, setPopularProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/popularinwomen')
        .then((response) => response.json())
        .then((data) => {
            setPopularProducts(data);
        })
        .catch((error) => {
            console.error('Error fetching data:', error);
        });
}, []);

  return (
    <div style={containerStyle}>
    <div className='popular' style={backgroundOverlayStyle}>
      <h1>POPULAR IN WOMEN</h1>
      <hr />
      <div className="popular-item">
        {popularProducts.map((item, i)=>{
            return <Item key={i} id={item.id} name={item.name}
            image={item.image} new_price={item.new_price}
            old_price={item.old_price}/>
        })}
      </div>
    </div>
    </div>
  )
}

export default Popular
